import React, { useEffect, useRef, useState, memo } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Animated,
  Dimensions
} from 'react-native';
import { colors } from '../theme/colors';

const { width } = Dimensions.get('window');

/**
 * SkillNode - N≈ìud individuel dans l'arbre de comp√©tences avec visuels am√©lior√©s
 * 
 * √âtats visuels drastiquement diff√©renci√©s :
 * - LOCKED : Noir, gris, ic√¥ne d√©satur√©e + cadenas
 * - UNLOCKED : Brillant, color√©, badge "NOUVEAU", animation pulse
 * - IN_PROGRESS : Bordure dor√©e, barre de progression circulaire
 * - COMPLETED : Vert vibrant, badge "‚úÖ Compl√©t√©", XP affich√©
 */
const SkillNode = ({ 
  program, 
  userProgress, 
  isUnlocked, 
  isCompleted, 
  onPress 
}) => {
  const pulseAnim = useRef(new Animated.Value(1)).current;
  const scaleAnim = useRef(new Animated.Value(1)).current;
  const pressAnim = useRef(new Animated.Value(1)).current;
  const [isPressed, setIsPressed] = useState(false);

  // D√©terminer l'√©tat du n≈ìud avec logique am√©lior√©e
  const getNodeState = () => {
    if (!isUnlocked && !isCompleted) return 'LOCKED';
    if (isCompleted) return 'COMPLETED';
    if (userProgress && userProgress.currentLevel > 1) return 'IN_PROGRESS';
    return 'UNLOCKED';
  };

  const nodeState = getNodeState();

  // Calculer le pourcentage de progression
  const getProgressPercentage = () => {
    if (!userProgress || !program.levels) return 0;
    return (userProgress.currentLevel - 1) / program.levels.length;
  };

  // Animation pulse pour les n≈ìuds UNLOCKED (plus visible)
  useEffect(() => {
    if (nodeState === 'UNLOCKED') {
      const startPulseAnimation = () => {
        Animated.sequence([
          Animated.timing(pulseAnim, {
            toValue: 1.08, // Plus prononc√©
            duration: 2000, // Plus lent
            useNativeDriver: true,
          }),
          Animated.timing(pulseAnim, {
            toValue: 1.0,
            duration: 2000,
            useNativeDriver: true,
          }),
        ]).start(() => startPulseAnimation());
      };
      startPulseAnimation();
    } else {
      // Arr√™ter l'animation pour les autres √©tats
      pulseAnim.setValue(1);
    }
  }, [nodeState, pulseAnim]);

  // Animation de pression am√©lior√©e
  const handlePressIn = () => {
    setIsPressed(true);
    Animated.spring(pressAnim, {
      toValue: 0.95,
      useNativeDriver: true,
    }).start();
  };

  const handlePressOut = () => {
    setIsPressed(false);
    Animated.spring(pressAnim, {
      toValue: 1,
      useNativeDriver: true,
    }).start();
  };



  // Couleurs par difficult√©
  const getDifficultyColor = (difficulty) => {
    const difficultyColors = {
      'D√©butant': '#4CAF50',
      'D√©butant+': '#8BC34A',
      'Interm√©diaire': '#FF9800',
      'Interm√©diaire+': '#FF5722',
      'Avanc√©': '#F44336',
      'Avanc√©+': '#E91E63',
      'Expert': '#9C27B0',
      'Elite': '#673AB7',
      'LEGEND': '#FFD700'
    };
    return difficultyColors[difficulty] || '#666';
  };

  // Styles dynamiques selon l'√©tat
  const getNodeStyles = () => {
    const baseStyle = {
      transform: [
        { scale: nodeState === 'UNLOCKED' ? pulseAnim : scaleAnim }
      ]
    };

    switch (nodeState) {
      case 'LOCKED':
        return {
          ...baseStyle,
          opacity: 0.4, // Tr√®s d√©sactiv√© visuellement
          backgroundColor: '#2a2a2a', // Fond gris fonc√©
        };
      case 'UNLOCKED':
        return {
          ...baseStyle,
          opacity: 1,
          shadowColor: program.color,
          shadowOffset: { width: 0, height: 0 },
          shadowOpacity: 0.6,
          shadowRadius: 8,
          elevation: 8,
          backgroundColor: program.color + '1A', // 10% opacity de la couleur du programme
        };
      case 'IN_PROGRESS':
        return {
          ...baseStyle,
          opacity: 1,
          backgroundColor: program.color + '33', // 20% opacity
        };
      case 'COMPLETED':
        return {
          ...baseStyle,
          opacity: 1,
          backgroundColor: program.color + '4D', // 30% opacity
          borderColor: colors.success,
          borderWidth: 2,
        };
      default:
        return baseStyle;
    }
  };

  const getBorderColor = () => {
    switch (nodeState) {
      case 'LOCKED':
        return '#444444'; // Bordure grise fonc√©e pour les verrouill√©s
      case 'UNLOCKED':
        return program.color;
      case 'IN_PROGRESS':
        return colors.warning;
      case 'COMPLETED':
        return colors.success;
      default:
        return colors.border;
    }
  };

  // Force la clicabilit√© pour "Fondations D√©butant"
  const isClickable = nodeState !== 'LOCKED' || program.id === 'beginner-foundation';

  // Debug pour Fondations D√©butant
  if (program.id === 'beginner-foundation') {
    console.log('üîç SkillNode Fondations:', {
      nodeState,
      isClickable,
      onPress: !!onPress
    });
  }

  return (
    <View style={styles.container}>
      <TouchableOpacity
        onPress={onPress} // Toujours actif maintenant
        onPressIn={isClickable ? handlePressIn : null}
        onPressOut={isClickable ? handlePressOut : null}
        disabled={false} // Jamais d√©sactiv√©
        activeOpacity={0.8}
      >
        <Animated.View 
          style={[
            styles.nodeContainer,
            { borderColor: getBorderColor() },
            getNodeStyles()
          ]}
        >
          {/* Ic√¥ne principale du programme */}
          <Text style={styles.programIcon}>{program.icon}</Text>

          {/* Barre de progression circulaire pour IN_PROGRESS */}
          {nodeState === 'IN_PROGRESS' && (
            <View style={styles.progressContainer}>
              <View 
                style={[
                  styles.progressBar,
                  { 
                    backgroundColor: program.color,
                    width: `${getProgressPercentage() * 100}%`
                  }
                ]} 
              />
            </View>
          )}

          {/* Badge d'√©tat en haut √† droite */}
          <View style={styles.badgeContainer}>
            {nodeState === 'LOCKED' && (
              <View style={[styles.badge, styles.lockedBadge]}>
                <Text style={styles.badgeIcon}>üîí</Text>
              </View>
            )}
            
            {nodeState === 'UNLOCKED' && (
              <View style={[styles.badge, styles.unlockedBadge]}>
                <Text style={styles.badgeIcon}>üîì</Text>
              </View>
            )}
            
            {nodeState === 'IN_PROGRESS' && (
              <View style={[styles.badge, styles.progressBadge]}>
                <Text style={styles.badgeIcon}>‚ö°</Text>
              </View>
            )}
            
            {nodeState === 'COMPLETED' && (
              <View style={[styles.badge, styles.completedBadge]}>
                <Text style={styles.badgeIcon}>‚úÖ</Text>
              </View>
            )}
          </View>
        </Animated.View>
      </TouchableOpacity>

      {/* Nom du programme */}
      <Text 
        style={[
          styles.programName,
          { opacity: nodeState === 'LOCKED' ? 0.5 : 1 } // D√©sactiv√© si verrouill√©
        ]}
        numberOfLines={2}
      >
        {program.name}
      </Text>

      {/* Difficult√© */}
      <Text 
        style={[
          styles.difficultyText,
          { 
            color: getDifficultyColor(program.difficulty),
            opacity: nodeState === 'LOCKED' ? 0.5 : 1 // D√©sactiv√© si verrouill√©
          }
        ]}
      >
        {program.difficulty}
      </Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    margin: 8,
    width: 100,
  },
  nodeContainer: {
    width: 80,
    height: 80,
    borderRadius: 40, // Cercle parfait
    borderWidth: 2,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: colors.surface,
    position: 'relative',
  },
  programIcon: {
    fontSize: 32,
    textAlign: 'center',
  },
  progressContainer: {
    position: 'absolute',
    bottom: -2,
    left: -2,
    right: -2,
    height: 4,
    backgroundColor: colors.border,
    borderRadius: 2,
    overflow: 'hidden',
  },
  progressBar: {
    height: '100%',
    borderRadius: 2,
  },
  badgeContainer: {
    position: 'absolute',
    top: -8,
    right: -8,
  },
  badge: {
    borderRadius: 12,
    paddingHorizontal: 6,
    paddingVertical: 2,
    minWidth: 24,
    alignItems: 'center',
    justifyContent: 'center',
  },
  lockedBadge: {
    backgroundColor: colors.surface,
    borderWidth: 1,
    borderColor: colors.border,
  },
  unlockedBadge: {
    backgroundColor: colors.primary,
    borderWidth: 1,
    borderColor: colors.accent,
  },
  progressBadge: {
    backgroundColor: colors.warning,
    paddingHorizontal: 8,
  },
  completedBadge: {
    backgroundColor: colors.success,
  },
  badgeIcon: {
    fontSize: 12,
  },
  badgeText: {
    fontSize: 10,
    color: colors.background,
    fontWeight: 'bold',
  },
  programName: {
    marginTop: 8,
    fontSize: 12,
    fontWeight: '600',
    color: colors.text,
    textAlign: 'center',
    lineHeight: 14,
  },
  difficultyText: {
    marginTop: 2,
    fontSize: 10,
    fontWeight: '500',
    textAlign: 'center',
  },
});

export default SkillNode;
