import { initializeApp, getApps, getApp } from 'firebase/app';
import { getAuth } from 'firebase/auth';
import { getFirestore } from 'firebase/firestore';

// Configuration Firebase - Configuration complète
const firebaseConfig = {
  apiKey: "AIzaSyAokpSG0MrYQVWYWXDQCOtVjM10ilYZRBA",
  authDomain: "hybridrpg-53f62.firebaseapp.com",
  projectId: "hybridrpg-53f62",
  storageBucket: "hybridrpg-53f62.appspot.com",
  messagingSenderId: "195554523219",
  appId: "1:195554523219:web:04e0abb1ecbdd38c926ea6"
};

// Initialiser Firebase - éviter la double initialisation
let app;
try {
  if (getApps().length === 0) {
    app = initializeApp(firebaseConfig);
  } else {
    app = getApp();
  }
} catch (error) {
  console.error('Erreur initialisation Firebase:', error);
  // Fallback: essayer d'initialiser quand même
  app = initializeApp(firebaseConfig);
}

// Initialiser les services
let auth;
let db;

try {
  auth = getAuth(app);
  db = getFirestore(app);
} catch (error) {
  console.error('Erreur initialisation services Firebase:', error);
  // Réessayer
  auth = getAuth(app);
  db = getFirestore(app);
}

// Exporter les services
export { auth, db };
export default app;
